css:
page { box-sizing: border-box; border: 10px solid black; }
hr {margin-top: 10px;
margin-bottom: 10px}
 #test {

      overflow: auto;
      left: 0;
      right: 0;
      top: 0;
      bottom: 0;
      border: 5px solid red;
    }

.border {
border: 1px solid #ccc;
}
.border-left {
border-left: 1px solid #ccc;
} 
.border-right {
border-right: 1px solid #ccc;
}
.border-bottom {
border-bottom: 1px solid #ccc;
}
@media screen {
.has-variables{
display: none;
}
}
@media print {
.print-fromat  {
 position: fixed;

    top: 0;

}

